import { useState } from "react";
import fetch from "isomorphic-unfetch";
import Link from "next/link";

const BASE_PATH =
  "http://virtserver.swaggerhub.com/ziroppe/WeWantToEatJiro/1.0.0";

const App = (props) => {
  return (
    <div>
      <h1>オンライン飲み会やろうぜ！！</h1>
      <ul>
        {props.categories.map((category: string, index: number) => {
          return (
            <Link href="/categories/[pid]">
              <li key={index}>{category}</li>
            </Link>
          );
        })}
      </ul>
    </div>
  );
};

App.getInitialProps = async ({ req }) => {
  // ルームの全てのカテゴリを取得
  const res = await fetch(BASE_PATH + "/categories");
  const data = await res.json();
  const categories: string[] = data.map((val) => {
    return val.name;
  });
  return { categories };
};
export default App;
