import fetch from "isomorphic-unfetch";
import Link from "next/link";

const BASE_PATH =
  "http://virtserver.swaggerhub.com/ziroppe/WeWantToEatJiro/1.0.0";

interface AppProps {
  categories: string[];
}

const App = (props: AppProps) => {
  return (
    <div>
      <h1>オンライン飲み会やろうぜ！！</h1>
      <ul>
        {props.categories.map((category: string, index: number) => {
          return (
            <li key={index}>
              <Link href={{ query: index }} as={`/categories/${index}`}>
                <a>{category}</a>
              </Link>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

App.getInitialProps = async ({ req }) => {
  // ルームの全てのカテゴリを取得
  const res = await fetch(BASE_PATH + "/categories");
  const data = await res.json();
  const categories: string[] = data.map((val) => {
    return val.name;
  });
  return { categories };
};
export default App;
