import fetch from "isomorphic-unfetch";
import Link from "next/link";
import { API_PATH } from "./env";

interface Room {
  name: string;
  admin: string;
  description: string;
  index: number;
}

const Categories = (props) => {
  return (
    <div>
      <ul>
        {props.rooms.map((room) => {
          return (
            <li key={index}>
              <Link
                href={{
                  pathname: "/categories",
                  query: { index: `${index}` },
                }}
                as={"/categories"}
              >
                <a>
                  <div>
                    名前: {name}, 管理者: {admin}
                  </div>
                  <div>説明: {description}</div>
                </a>
              </Link>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

Categories.getInitialProps = async ({ query }) => {
  // indexを元にapi叩く
  // ex: /categories/1/rooms (カテゴリID = 1の部屋全て)
  const res = await fetch(API_PATH + "/categories/" + query.name + "/rooms");
  const resJson = await res.json();
  console.log(resJson);
  const rooms = resJson.map((room) => {
    // roomには名前, 管理者名, 詳細あたりがあればよさそう
    return {
      name: room.name,
      admin: room.admin,
      description: room.description,
    };
  });
  return { rooms };
};
export default Categories;
