import fetch from "isomorphic-unfetch";
import Link from "next/link";
import { API_PATH } from "./env";

interface Room {
  name: string;
  admin: string;
  description: string;
}

const Categories = (props) => {
  return (
    <div>
      <ul>
        {props.rooms.map((room: Room, index: number) => {
          return (
            <li key={index}>
              <Link href="/">
                <a>
                  <div>名前: {room.name}</div>
                  <div>, 管理者: {room.admin}</div>
                  <div>説明: {room.description}</div>
                </a>
              </Link>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

Categories.getInitialProps = async ({ query }) => {
  // indexを元にapi叩く
  // ex: /categories/1/rooms (カテゴリID = 1の部屋全て)
  const res = await fetch(API_PATH + "/categories/" + query.name + "/rooms");
  const resJson = await res.json();
  console.log(resJson);
  const rooms = resJson.map((room) => {
    // roomには名前, 管理者名, 詳細あたりがあればよさそう
    return {
      name: room.name,
      admin: room.admin,
      description: room.description,
    };
  });
  return { rooms };
};
export default Categories;
