import { useState, useEffect } from "react";
import Peer from "skyway-js";

import { API_PATH, SKYWAY_API_KEY } from "./env";

const Rooms = (props) => {
  const getRoomModeByHash = () => (location.hash === "#sfu" ? "sfu" : "mesh");
  navigator.mediaDevices
    .getUserMedia({
      audio: true,
      video: true,
    })
    .then((stream) => {
      document.getElementById("local-stream").srcObject = stream;
      setLocalVideoStream(stream);
    });
  const [roomMode, setRoomMode] = useState(getRoomModeByHash());
  const [localVideoMuted, setLocalVideoMuted] = useState(true);
  const [localVideoStream, setLocalVideoStream] = useState();

  useEffect(() => {
    const peer = new Peer({ key: SKYWAY_API_KEY });
    window.addEventListener("hashchange", () =>
      setRoomMode(getRoomModeByHash())
    );
  }, []);

  return (
    <div>
      <div className="container">
        <h1 className="heading">Room Example タイトル</h1>
        <div className="room">
          <div>
            <video
              id="local-stream"
              muted={localVideoMuted}
              ref={async (video) => {
                // Cannot set property 'srcObject' of null問題
                video.srcObject = await navigator.mediaDevices.getUserMedia({
                  audio: true,
                  video: true,
                });
              }}
            ></video>
            <span id="room-mode">{roomMode}</span>
            <input type="text" placeholder="Room Name" id="js-room-id" />
            <button id="join-trigger">Join</button>
            <button id="leave-trigger">Leave</button>
          </div>
          <div className="remote-streams" id="js-remote-streams"></div>
          <div>
            <pre className="messages" id="messages"></pre>
            <input type="text" id="local-text" />
            <button id="send-trigger">Send</button>
          </div>
        </div>
      </div>
      <ul></ul>
    </div>
  );
};

Rooms.getInitialProps = async ({ query }) => {
  return 1;
};

export default Rooms;
