import { useState } from "react";
import fetch from "isomorphic-unfetch";

const BASE_PATH =
  "http://virtserver.swaggerhub.com/ziroppe/WeWantToEatJiro/1.0.0";

const App = ({ categories }) => {
  return (
    <div>
      <ul>
        {categories.map((category: string, index: number) => {
          return <li key={index}>{category}</li>;
        })}
      </ul>
    </div>
  );
};

App.getInitialProps = async ({ req }) => {
  // ルームの全てのカテゴリを取得
  const res = await fetch(BASE_PATH + "/categories");
  const data = await res.json();
  const categories: string[] = data.map((val) => {
    return val.name;
  });
  return { categories };
};
export default App;
